      />
      <SvgBlock  svg="Design.svg" />
    </Content>
    <Content>
      <TextBlock
        topic="Design"
        title="We build award winning Designs"
        subText="We help clients to build great design to attract more customers"
      />
      <SvgBlock  svg="Design.svg" />
    </Content>
    <Content>
      <TextBlock
        topic="Design"
        title="We build award winning Designs"
        subText="We help clients to build great design to attract more customers"
      />
      <SvgBlock  svg="Design.svg" />
    </Content>
    
    </ServiceSection>
  );
};

export default Services;
