// This is Testimonials component
import styled from "styled-components";
import Slider from "react-slick";

import "../../../node_modules/slick-carousel/slick/slick.css";
import "../../../node_modules/slick-carousel/slick/slick-theme.css";
import Card from "../../components/Card";

const Section = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
width: 100%;
padding: 5rem 0;
`;
const Title = styled.h1`
display: inline-block;
font-size: 2rem;
margin-top: 1rem;
position: relative;

&::before {
  content: "";
  height: 1px;
  width: 50%;
  position: absolute;
  left: 50%;
  bottom: 0;
  border-bottom: 2px solid var(--kahve);
  transform: translate(-50%);

}
`;
const Carousal = styled.div`
width: 50vw;
display: flex;
flex-direction: column;
justify-content: center;
`;

const Testimonials = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return <Section>

    <Title>Few good words about us!</Title>
    <Carousal>
      <Slider {...settings}>
        <Card
          text="CodeBucks has been essential part of our business."
          name="Ersin Kılıçoğlu"
          image="avatar-1"
        />
        <Card
          text="CodeBucks has been essential part of our business."
          name="Jenny (CodeCall)"
          image="avatar-1"
        />
        <Card
          text="CodeBucks has been essential part of our business."
          name="Jenny (CodeCall)"
          image="avatar-1"
        />
        <Card
          text="CodeBucks has been essential part of our business."
          name="Jenny (CodeCall)"
          image="avatar-1"
        />
      </Slider>
    </Carousal>
  </Section>;
};

export default Testimonials;
