// This is Testimonials component
import styled from "styled-components";
import Slider from "react-slick";

import "../../../node_modules/slick-carousel/slick/slick.css";
import "../../../node_modules/slick-carousel/slick/slick-theme.css";
import Card from "../../components/Card";

const Section = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
width: 100%;
padding: 5rem 0;
`;
const Title = styled.h1`
display: inline-block;
font-size: calc(1rem + 1.5vw);
margin-top: 1rem;
position: relative;

&::before {
  content: "";
  height: 1px;
  width: 50%;
  position: absolute;
  left: 50%;
  bottom: 0;
  border-bottom: 2px solid var(--kahve);
  transform: translate(-50%);

}
`;
const Carousal = styled.div`
width: 50vw;
display: flex;
flex-direction: column;
justify-content: center;

@media only Screen and (max-width:40em){
  width:90vw;
}

.slick-slider .slick-arrow:before{
  color: var(--black);
  font-size: 1.5rem;
  @media only Screen and (max-width:40em){
    display: none;
  }
}
.slick-slider .slick-dots button:before{
  color: var(--black);
  font-size: 1.5rem;
}
.slick-slide.slick-active{
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin: 0;
  padding: 0;
  margin-bottom: 3rem;
}
`;

const Testimonials = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return <Section>

    <Title>Hakkımızda birkaç güzel söz!</Title>
    <Carousal>
      <Slider {...settings}>
        <Card
          text="7Proje müşterilerime karşı her zaman anlayışlı olmuştur ve bana yaptırdığı işlerde gerekli tüm dökümanları düzenli bir biçimde sağlamıştır."
          name="Ersin Kılıçoğlu (Graphic Designer)"
          image="avatar-1"
        />
        <Card
          text="7Proje ile kodlama yaparken çok eğleniyoruz."
          name="Oğuz Karakoç (Coder)"
          image="avatar-2"
        />
        <Card
          text="7Proje sanatçı arkadaşların kayıtlarını ve cliplerini düzenlerken tüm detayları hallediyor bize eğlenmek kalıyor."
          name="Şahin Ocaklı (Sound artist)"
          image="avatar-3"
        />
        <Card
          text="7Proje ile uzaktan çalışıyoruz ve istediğimiz zaman ulaşabiliyoruz."
          name="Defne Aydoğdu (Graphic Designer)"
          image="avatar-4"
        />
      </Slider>
    </Carousal>
  </Section>;
};

export default Testimonials;
